$clr-xs							:#9b59b6;
$clr-sm							:#f39c12;
$clr-md							:#1abc9c;
$clr-lg							:#d6080d;
$clr-xl							:#3498db;

// Cubic
$ease-in-cubic					:cubic-bezier(0.550, 0.055, 0.675, 0.190);
$ease-out-cubic					:cubic-bezier(0.215, 0.610, 0.355, 1.000);
$ease-in-out-cubic				:cubic-bezier(0.645, 0.045, 0.355, 1.000);

// Circ
$ease-in-circ					:cubic-bezier(0.600, 0.040, 0.980, 0.335);
$ease-out-circ					:cubic-bezier(0.075, 0.820, 0.165, 1.000);
$ease-in-out-circ				:cubic-bezier(0.785, 0.135, 0.150, 0.860);

// Expo
$ease-in-expo					:cubic-bezier(0.950, 0.050, 0.795, 0.035);
$ease-out-expo					:cubic-bezier(0.190, 1.000, 0.220, 1.000);
$ease-in-out-expo				:cubic-bezier(1.000, 0.000, 0.000, 1.000);

// Quad
$ease-in-quad					:cubic-bezier(0.550, 0.085, 0.680, 0.530);
$ease-out-quad					:cubic-bezier(0.250, 0.460, 0.450, 0.940);
$ease-in-out-quad				:cubic-bezier(0.455, 0.030, 0.515, 0.955);

// Quart
$ease-in-quart					:cubic-bezier(0.895, 0.030, 0.685, 0.220);
$ease-out-quart					:cubic-bezier(0.165, 0.840, 0.440, 1.000);
$ease-in-out-quart				:cubic-bezier(0.770, 0.000, 0.175, 1.000);

// Quint
$ease-in-quint					:cubic-bezier(0.755, 0.050, 0.855, 0.060);
$ease-out-quint					:cubic-bezier(0.230, 1.000, 0.320, 1.000);
$ease-in-out-quint				:cubic-bezier(0.860, 0.000, 0.070, 1.000);

// Sine
$ease-in-sine					:cubic-bezier(0.470, 0.000, 0.745, 0.715);
$ease-out-sine					:cubic-bezier(0.390, 0.575, 0.565, 1.000);
$ease-in-out-sine				:cubic-bezier(0.445, 0.050, 0.550, 0.950);

// Back
$ease-in-back					:cubic-bezier(0.600, -0.280, 0.735, 0.045);
$ease-out-back					:cubic-bezier(0.175, 0.885, 0.320, 1.275);
$ease-in-out-back				:cubic-bezier(0.680, -0.550, 0.265, 1.550);

// iPad Any
$device-ipad-any				:"only screen and (min-device-width:768px) and (max-device-width:1024px)";
$device-ipad-any-landscape		:"only screen and (min-device-width:768px) and (max-device-width:1024px) and (orientation:landscape)";
$device-ipad-any-portrait		:"only screen and (min-device-width:768px) and (max-device-width:1024px) and (orientation:portrait)";

// iPad 3G + 4G
$device-ipad-4g					:"only screen and (min-device-width:768px) and (max-device-width:1024px) and (-webkit-min-device-pixel-ratio:2)";
$device-ipad-4g-landscape		:"only screen and (min-device-width:768px) and (max-device-width:1024px) and (orientation:landscape) and (-webkit-min-device-pixel-ratio:2)";
$device-ipad-4g-portrait		:"only screen and (min-device-width:768px) and (max-device-width:1024px) and (orientation:portrait) and (-webkit-min-device-pixel-ratio:2)";

// iPad 1G + 2G
$device-ipad-2g					:"only screen and (min-device-width:768px) and (max-device-width:1024px) and (-webkit-min-device-pixel-ratio:1)";
$device-ipad-2g-landscape		:"only screen and (min-device-width:768px) and (max-device-width:1024px) and (orientation:landscape) and (-webkit-min-device-pixel-ratio:1)";
$device-ipad-2g-portrait		:"only screen and (min-device-width:768px) and (max-device-width:1024px) and (orientation:portrait) and (-webkit-min-device-pixel-ratio:1)";

// iPad Mini
$device-ipad-mini-any			:"and (min-device-width:768px) and (max-device-width:1024px) and (-webkit-min-device-pixel-ratio:1)";
$device-ipad-mini-any-landscape	:"and (min-device-width:768px) and (max-device-width:1024px) and (orientation:landscape) and (-webkit-min-device-pixel-ratio:1)";
$device-ipad-mini-any-portrait	:"and (min-device-width:768px) and (max-device-width:1024px) and (orientation:portrait) and (-webkit-min-device-pixel-ratio:1)";

// iPhone 5
$device-iphone5					:"only screen and (min-device-width:320px) and (max-device-width:568px)";
$device-iphone5-landscape		:"only screen and (min-device-width:320px) and (max-device-width:568px) and (orientation:landscape)";
$device-iphone5-portrait		:"only screen and (min-device-width:320px) and (max-device-width:568px) and (orientation:portrait)";

// iPhone 2G, 3G, 4, 4S
$device-iphone					:"only screen and (min-device-width:320px) and (max-device-width:480)";
$device-iphone-landscape		:"only screen and (min-device-width:320px) and (max-device-width:480) and (orientation:landscape)";
$device-iphone-portrait			:"only screen and (min-device-width:320px) and (max-device-width:480) and (orientation:portrait)";

// iPhone 6
$device-iphone6					:"only screen and (min-device-width:375px) and (max-device-width:667px)";
$device-iphone6-landscape		:"only screen and (min-device-width:375px) and (max-device-width:667px) and (orientation:landscape)";
$device-iphone6-portrait		:"only screen and (min-device-width:375px) and (max-device-width:667px) and (orientation:portrait)";

// iPhone 6+
$device-iphone6plus				:"only screen and (min-device-width:414px) and (max-device-width:736px)";
$device-iphone6plus-landscape	:"only screen and (min-device-width:414px) and (max-device-width:736px) and (orientation:landscape)";
$device-iphone6plus-portrait	:"only screen and (min-device-width:414px) and (max-device-width:736px) and (orientation:portrait)";

// iPhone 6 and 6+
$device-iphone6all				:"only screen and (max-device-width:640px), only screen and (max-device-width:667px), only screen and (max-width:480px)";
$device-iphone6all-portrait		:"only screen and (max-device-width:640px), only screen and (max-device-width:667px), only screen and (max-width:480px) and (orientation:portrait)";
$device-iphone6all-landscape	:"only screen and (max-device-width:640px), only screen and (max-device-width:667px), only screen and (max-width:480px) and (orientation:landscape)";

