legend
{
	padding						:0;
	font-weight					:inherit;
	display						:block;
	width						:100%;
	border						:0;
	line-height					:inherit;
}

label
{
	// Force IE8 to wrap long content
	max-width					:100%;
	display						:$fh-display;
	position 					:relative;
	vertical-align				:middle;
	//margin-bottom				:5px;
	font-weight					:600;

	a
	{
		font-weight				:bold;
	}

