	{
		display					:none;
	}
}

input[type="text"],
input[type="email"],
input[type="password"],
input[type="search"],
input[type="color"],
input[type="date"],
input[type="datetime"],
input[type="datetime-local"],
input[type="month"],
input[type="number"],
input[type="tel"],
input[type="time"],
input[type="url"],
input[type="week"],
input[list],
input[type="file"],
select,
textarea
{
	width						:$fh-input-width;
	padding 					:$fh-input-padding;
	border-radius				:$fh-border-radius;
	background-color			:$fh-input-bg-color;
	border						:$fh-border-width $fh-border-style $fh-border-color;

	&:active,
	&:focus
	{
		cursor 					:text;
		outline 				:none;
	}
}

input[aria-invalid="true"]
{
	border-color				:$fh-invalid-border-color;
}

input[aria-invalid="false"]
{
	border-color				:$fh-valid-border-color;
}

input[type="text"], input[type="email"], input[type="password"], input[type="search"], input[type="color"], input[type="date"], input[type="datetime"], input[type="datetime-local"], input[type="month"], input[type="number"], input[type="tel"], input[type="time"], input[type="url"], input[type="week"], input[list]
{
	-webkit-appearance			:none;
}

textarea
{
	-webkit-appearance			:none;
	resize 						:vertical;
	overflow 					:auto;
	// Reset height for `textarea`s
	height						:auto;
}

input[type="range"]
{
	width						:$fh-input-width;
	display						:block;
	-webkit-appearance			:none;

	$width:100%;
	$track-height:12px;
	$track-border-radius:0px;
	$track-background-color:#ac51b5;

	&::-webkit-slider-runnable-track
	{
		width					:$width;
		height					:$track-height;
		cursor					:pointer;
		animate					:0.2s;
		box-shadow				:0px 0px 0px #000000, 0px 0px 0px #0d0d0d;
		background				:$track-background-color;
		border-radius			:$track-border-radius;
		border					:0px solid #000101;
	}

	&:in-range::-webkit-slider-runnable-track
	{

	}

	&:out-of-range::-webkit-slider-runnable-track
	{

	}

	&::-webkit-slider-thumb
	{
		-webkit-appearance		:none;
		box-shadow				:0px 0px 0px #000000, 0px 0px 0px #0d0d0d;
		border					:0px solid #000000;
		height					:20px;
		width					:39px;
		border-radius			:7px;
		background				:#65001c;
		cursor					:pointer;
		margin-top				:-3.6px;
	}

	&:focus::-webkit-slider-runnable-track
	{
		background				:$track-background-color;
	}

	&::-moz-range-track
	{
		width					:$width;
		height					:$track-height;
		cursor					:pointer;
		animate					:0.2s;
		box-shadow				:0px 0px 0px #000000, 0px 0px 0px #0d0d0d;
		background				:$track-background-color;
		border-radius			:$track-border-radius;
		border					:0px solid #000101;
	}

	&::-moz-range-thumb
	{
		box-shadow				:0px 0px 0px #000000, 0px 0px 0px #0d0d0d;
		border					:0px solid #000000;
		height					:20px;
		width					:39px;
		border-radius			:7px;
		background				:#65001c;
		cursor					:pointer;
	}

	&::-ms-track
	{
		width					:$width;
		height					:$track-height;
		cursor					:pointer;
		animate					:0.2s;
		background				:transparent;
		border-color			:transparent;
		border-width			:39px 0;
		color					:transparent;
	}

	&::-ms-fill-lower
	{
		background				:$track-background-color;
		border					:0px solid #000101;
		border-radius			:50px;
		box-shadow				:0px 0px 0px #000000, 0px 0px 0px #0d0d0d;
	}

	&::-ms-fill-upper
	{
		background				:$track-background-color;
		border					:0px solid #000101;
		border-radius			:50px;
		box-shadow				:0px 0px 0px #000000, 0px 0px 0px #0d0d0d;
	}

	&::-ms-thumb
	{
		box-shadow				:0px 0px 0px #000000, 0px 0px 0px #0d0d0d;
		border					:0px solid #000000;
		height					:20px;
		width					:39px;
		border-radius			:7px;
		background				:#65001c;
		cursor					:pointer;
	}

	&:focus::-ms-fill-lower
	{
		background				:$track-background-color;
	}

	&:focus::-ms-fill-upper
	{
		background				:$track-background-color;
	}
}

input[type="file"]
{
	display						:block;
}

/*
 * 1. Search inputs in iOS
 *	  This overrides the extra rounded corners on search inputs in iOS so that
 *	  our styles class can properly style them. Note that this cannot simply be
 *	  added to `.form-control` as it's not specific enough
 */
input[type="search"]
{
	box-sizing					:border-box;
	-webkit-appearance			:none; /* 1 */
}

input[type="search"]::-webkit-search-cancel-button,
input[type="search"]::-webkit-search-decoration
{
	-webkit-appearance			:none;
}

select
{
	@if ($fh-allow-vendor-styling == false)
	{
		-webkit-appearance		:none;
		-moz-appearance			:none;
		-ms-appearance			:none;
		-o-appearance			:none;
	}

	option
	{
		padding 				:$fh-input-padding;
	}

	&[multiple],
	&[size]
	{
		// Make multiple select elements height not fixed
		height					:auto;
		padding					:0;

		option
		{
			margin				:0;
		}
	}
}

label, input, textarea, button, select, option
{
	cursor 						:pointer;
}

button,
input[type="button"],
input[type="submit"],
input[type="reset"],
input[type="image"]
{

}

input[type="image"]
{
	text-align					:center;
	padding 					:$fh-input-padding;
}

input[type="text"],
input[type="email"],
input[type="password"],
input[type="search"],
input[type="color"],
input[type="date"],
input[type="datetime"],
input[type="datetime-local"],
input[type="month"],
input[type="number"],
input[type="tel"],
input[type="time"],
input[type="url"],
input[type="week"],
input[list],
input[type="file"],
select,
textarea,
textarea,
select,
option,
button
{
	&[disabled]
	{
		cursor					:not-allowed;
	}

	&:hover
	{
		background-color		:$fh-hover-bg-color;
		border-color			:$fh-hover-border-color;
	}

	&:focus
	{
		background-color		:$fh-focus-bg-color;
		border-color			:$fh-focus-border-color;
	}
}

input[type="checkbox"],
input[type="radio"]
{
	margin						:0 5px 0 0;
	margin-top					:1px \9; // IE8-9
	line-height					:normal;

	// make sure that these always display inline - as they should be next to an input
	display						:inline-block;
	vertical-align				:middle;

	+ label
	{
		width 					:auto;
		display 				:inline-block;
		margin-top				:0;
		margin-bottom			:0;
	}

	&[disabled]
	{
		+ label
		{
			cursor				:not-allowed;
		}
	}

	&:focus
	{
		outline					:0;
	}
}

button,
input[type="button"],
input[type="submit"],
input[type="reset"]
{
	&:hover,
	&:focus
	{
		background-color	 	:$fh-button-hover-bg-color;
		color					:$fh-button-hover-font-color;
	}
}

progress
{
	appearance					:none;
	height						:20px;
	display 					:block;
	width 						:100%;

	$background 				:#eee;
	$progress 					:#CC0000;

	// firefox
	border						:none;
	background					:$background;
	border-radius				:$fh-border-radius;
	box-shadow					:0 2px 3px rgba(0,0,0,0.2) inset;

	&::-webkit-progress-bar
	{
		background				:$background;
		border-radius			:$fh-border-radius;
		box-shadow				:0 2px 3px rgba(0,0,0,0.2) inset;
	}

	&::-webkit-progress-value
	{
		background-color		:$progress;
		border-radius			:$fh-border-radius;
	}

	&::-moz-progress-bar
	{
		background-color		:$progress;
		border-radius			:$fh-border-radius;
	}
}

meter
{
	appearance					:none;
	height						:20px;
	display 					:block;
	width 						:100%;

	$optimum 					:$clr-success;
	$sub-optimum 				:$clr-warning;
	$sub-sub-optimum 			:#CC4600;
	$background 				:#eee;

	// firefox
	background					:$background;
	border-radius				:$fh-border-radius;
	box-shadow					:0 2px 3px rgba(0,0,0,0.2) inset;

	&::-webkit-meter-bar
	{
		background				:$background;
		border-radius			:$fh-border-radius;
		box-shadow				:0 2px 3px rgba(0,0,0,0.2) inset;
	}

	&::-webkit-meter-optimum-value, &::-webkit-meter-suboptimum-value, &::-webkit-meter-even-less-good-value
	{
		border-radius			:$fh-border-radius;
	}

	&::-webkit-meter-optimum-value
	{
		background				:$optimum;
	}

	&::-webkit-meter-suboptimum-value
	{
		background				:$sub-optimum;
	}

	&::-webkit-meter-even-less-good-value
	{
		background				:$sub-sub-optimum;
	}

	// firefox
	&::-moz-meter-bar
	{
		border-radius			:$fh-border-radius;
	}

	&:-moz-meter-optimum::-moz-meter-bar
	{
		background				:$optimum;
	}

	&:-moz-meter-sub-optimum::-moz-meter-bar
	{
		background				:$sub-optimum;
	}

	&:-moz-meter-sub-sub-optimum::-moz-meter-bar
	{
		background				:$sub-sub-optimum;
	}
}

input,
textarea
{
	@include placeholder
	{
		color 					:$fh-placeholder-colour;
	}
}

input[type="color"]
{
	padding 					:0;
	width 						:auto;
	min-width					:1.5rem;
}

/*
output

Represents the result of a calculation.
The optional for attribute allows an explicit relationship to be made between the result of a calculation and the elements representing the values that influenced the calculation. If specified, the for attribute must contain a string consisting of an unordered set of unique, space-separated tokens, each of which must be the ID of an element in the same document.

Markup:
<form>
	<label for="flying">Flying Skill Level</label>
	<input name="flying-level" id="flying" type="range" min="0" max="250">
	<output for="flying" onforminput="value = flying.valueAsNumber">0</output>
</form>

*/
output
{
	display						:block;
}

