	&:in-range::-webkit-slider-runnable-track
	{

	}

	&:out-of-range::-webkit-slider-runnable-track
	{

	}

	&::-webkit-slider-thumb
	{
		-webkit-appearance		:none;
		box-shadow				:0px 0px 0px #000000, 0px 0px 0px #0d0d0d;
		border					:0px solid #000000;
		height					:20px;
		width					:39px;
		border-radius			:7px;
		background				:#65001c;
		cursor					:pointer;
		margin-top				:-3.6px;
	}

	&:focus::-webkit-slider-runnable-track
	{
		background				:$track-background-color;
	}

	&::-moz-range-track
	{
		width					:$width;
		height					:$track-height;
		cursor					:pointer;
		animate					:0.2s;
		box-shadow				:0px 0px 0px #000000, 0px 0px 0px #0d0d0d;
		background				:$track-background-color;
		border-radius			:$track-border-radius;
		border					:0px solid #000101;
	}

	&::-moz-range-thumb
	{
		box-shadow				:0px 0px 0px #000000, 0px 0px 0px #0d0d0d;
		border					:0px solid #000000;
		height					:20px;
		width					:39px;
		border-radius			:7px;
		background				:#65001c;
		cursor					:pointer;
	}

	&::-ms-track
	{
		width					:$width;
		height					:$track-height;
		cursor					:pointer;
		animate					:0.2s;
		background				:transparent;
		border-color			:transparent;
		border-width			:39px 0;
		color					:transparent;
	}

	&::-ms-fill-lower
	{
		background				:$track-background-color;
		border					:0px solid #000101;
		border-radius			:50px;
		box-shadow				:0px 0px 0px #000000, 0px 0px 0px #0d0d0d;
	}

	&::-ms-fill-upper
	{
