@mixin arrow($box-edge				 : bottom,
								 $edge-side				: center,
								 $arrow-size			 : 10px,
								 $edge-side-offset : 0,
								 $fill-color			 : black,
								 $border-color		 : none,
								 $border-style		 : border) {

	// initiate internal variables
	$box-edge-inverse: bottom;
	$edge-side-pos: $edge-side;
	$edge-side-pos-value: 0;

	// establish inverse variables
	@if			$box-edge == top		{ $box-edge-inverse: bottom; }
	@else if $box-edge == right	{ $box-edge-inverse: left;	 }
	@else if $box-edge == bottom { $box-edge-inverse: top;		}
	@else if $box-edge == left	 { $box-edge-inverse: right;	}

	// calculate remaining internal variables
	@if ($box-edge == 'top' or $box-edge == 'bottom')
	{
		@if ($edge-side == center)
		{
			$edge-side-pos 			:left;
			$edge-side-pos-value 	:50%;
			$edge-side-offset 		:(-$arrow-size);
		}
	}

	@if ($box-edge == 'left' or $box-edge == 'right')
	{
		@if ($edge-side == center)
		{
			$edge-side-pos 			:top;
			$edge-side-pos-value 	:50%;
			$edge-side-offset 		:(-$arrow-size);
		}
	}

	&:after, &:before
	{
		content: " ";
		height: 0;
		width: 0;
		position: absolute;
		pointer-events: none;
		border: solid rgba(#fff, 0);
	}
	/**
	&:after
	 {
		border-color 							:rgba(#fff, 0);
		border-width 							:$arrow-size - 1;
		#{$box-edge} 							:(-$arrow-size) - $arrow-size + 2;
		border-#{$box-edge-inverse}-color 		:$fill-color;
		#{$edge-side-pos} 						:$edge-side-pos-value;
		margin-#{$edge-side-pos} 				:$edge-side-offset + 1;
	}

	@if $border-style == flag
	{
		border-radius 							:2px;

		&:before
		{
			border-color 						:rgba(#fff, 0);
			border-width 						:$arrow-size - 1;
			#{$box-edge} 						:(-$arrow-size) - $arrow-size + 2;
			border-#{$box-edge-inverse}-color 	:$border-color;
			#{$edge-side-pos} 					:$edge-side-pos-value;
			margin-#{$edge-side-pos} 			:$edge-side-offset + 2;
		}
	}
	@else
	{
		&:before
		{
			border-color 						:rgba(#fff, 0);
			border-width 						:$arrow-size;
			#{$box-edge} 						:(-$arrow-size) - $arrow-size;
			border-#{$box-edge-inverse}-color 	:$border-color;
			#{$edge-side-pos} 					:$edge-side-pos-value;
			margin-#{$edge-side-pos} 			:$edge-side-offset;
		}
	}
	*/
}

@mixin core-dropcap
(
	$float-position		:$core-dropcap-float-position,
	$font-size			:$core-dropcap-font-size,
	$font-family		:$core-dropcap-font-family,
	$txt-indent			:$core-dropcap-txt-indent,
	$margin				:$core-dropcap-margin,
	$padding			:$core-dropcap-padding,
	$color				:$core-dropcap-color,
	$line-height		:$core-dropcap-line-height,
	$bg 				:$core-dropcap-bg
)
{
	&:first-letter
	{
		float 			:$float-position;
		margin 			:$margin;
		padding 		:$padding;
		font-size 		:$font-size;
		font-family 	:$font-family;
		line-height 	:$line-height;
		text-indent 	:$txt-indent;
		background	 	:$bg;
		color			:$color;
	}
}

%icon
{
	speak 					:none;
	line-height 			:1;
	text-transform 			:none;
	display 				:inline-block;
	vertical-align 			:middle;
	font-weight 			:normal;
	font-style 				:normal;
	font-variant 			:normal;
	text-decoration 		:none;
	text-rendering 			:optimizeLegibility;
	-webkit-font-smoothing 	:antialiased;
	-moz-osx-font-smoothing	:grayscale;
}

@mixin icon-only($content)
{
	$icon-font-name			:false !default;

	@if($icon-font-name)
	{
		font-family 		:$icon-font-name;
	}

	@if($content)
	{
		content 			:$content;
	}
}

@mixin icon-font($content:false)
{
	$icon-font-name			:false !default;

	@if($icon-font-name)
	{
		font-family 			:$icon-font-name;

		@if($content)
		{
			content 			:$content;
		}

		speak 					:none;
		line-height 			:1;
		text-transform 			:none;
		display 				:inline-block;
		vertical-align 			:middle;
		font-weight 			:normal;
		font-style 				:normal;
		font-variant 			:normal;
		text-decoration 		:none;
		text-rendering 			:optimizeLegibility;
		-webkit-font-smoothing 	:antialiased;
		-moz-osx-font-smoothing	:grayscale;
	}
}

@mixin icon-before($content)
{
	&:before
	{
		@include icon-font($content);

		margin-right 			:8px;
	}
}

@mixin icon-after($content)
{
	&:after
	{
		@include icon-font($content);

		margin-left 			:8px;
	}
}

@mixin icon($content)
{
	@include icon-font($content);
}

@mixin mq-between($from, $to)
{
	@if map-has-key($breakpoints, $from) and map-has-key($breakpoints, $to)
	{
		$f			:map-get($breakpoints, $from);
		$t			:map-get($breakpoints, $to);

		@media screen and (min-width: $f) and (max-width: ($t - 1)) { @content; }
	}
	@else
	{
		@warn "Unfortunately, no value could be retrieved from `#{$breakpoint}`. Please make sure it is defined in `$breakpoints` map.";
	}
}

@mixin respond-to($query)
{
	@if map-has-key($breakpoints, $query)
	{
		$t			:map-get($breakpoints, $query);

		@media screen and (max-width: ($t - 1)) { @content; }
	}
	@else
	{
		@warn "Unfortunately, no value could be retrieved from `#{$breakpoint}`. Please make sure it is defined in `$breakpoints` map.";
	}
}

@mixin mq-to($query)
{
	@include respond-to($query)
	{
		@content;
	}
}

@mixin breakpoint($query: false)
{
	@if($query == xs)
	{
		@content;
	}
	@else if($query == false)
	{
		@content;
	}
	@else if map-has-key($breakpoints, $query)
	{
		@media screen and (min-width: #{map-get($breakpoints, $query)}) { @content; }
	}
	@else
	{
		@warn "Unfortunately, no value could be retrieved from `#{$breakpoint}`. Please make sure it is defined in `$breakpoints` map.";
	}
}

@mixin mq($query)
{
	@include breakpoint($query)
	{
		@content;
	}
}

@mixin retina()
{
	@media (-webkit-min-device-pixel-ratio: 1.3), (-o-min-device-pixel-ratio: 2.6 / 2), (min--moz-device-pixel-ratio: 1.3), (min-device-pixel-ratio: 1.3), (min-resolution: 1.3dppx)
	{
		@content;
	}
}

@function media-query-prefix($size)
{
	@if($size == nth($the_breakpoints, 1))
	{
		$prefix 		:"";
	}
	@else
	{
		$prefix 		:"#{$size}-";
	}

	@return $prefix;
}

@mixin query-layout($base, $breakpoints: $the_breakpoints)
{
	@each $size in $breakpoints
	{
		$prefix 		:media-query-prefix($size);

		@include mq($size)
		{
			.#{$prefix}#{$base}
			{
				@content;
			}
		}
	}
}

