{
	word-wrap 					:normal;
}

%core-inherit-wrap
{
	word-wrap 					:auto;
}

%clearfix
{
	&:after
	{
		content 				:"";
		display 				:table;
		clear					:both;
	}
}

// In some cases it's necessary to remove the line height on an element
// this does just that, then it resets it on the children
%zero-height
{
	line-height 			:0;

	> *
	{
		line-height 		:$base-line-height;
	}
}

%auto-margins
{
	margin-left				:auto;
	margin-right			:auto;
}

%zero-vertical-margins
{
	margin-top 				:0;
	margin-bottom			:0;
}

%list-unstyled
{
	margin 					:0;
	padding 				:0;
	list-style				:none;
}

%screen-reader
{
	position				:absolute;
	left					:-10000px;
	top						:auto;
	width					:1px;
	height					:1px;
	overflow				:hidden;
}

%is-hidden
{
	position 				:absolute;
	overflow 				:hidden;
	width 					:1px;
	height 					:1px;
	padding 				:0;
	border 					:0;
	clip 					:rect(1px, 1px, 1px, 1px);
}

%clearfix
{
	zoom 					:1;

	&:before, &:after
	{
		content 			:"";
		display 			:table;
	}

	&:after
	{
		clear 				:both;
	}
}

%webkit-appearance-reset
{
	-webkit-appearance 		:none;
}

%image-replacement
{
	display					:inline-block;
	border					:0;
	text-indent				:-999em;
	overflow				:hidden;
	background-color		:transparent;
	background-repeat		:no-repeat;
	text-align				:left;
	direction				:ltr;
	*line-height			:0;
}

%visually-hide
{
	border 					:0;
	// Modern syntax for clip used - intentially not supporting ie7
	clip 					:rect(0, 0, 0, 0);
	height 					:1px;
	margin 					:-1px;
	overflow 				:hidden;
	padding 				:0;
	position 				:absolute;
	width 					:1px;
}

%visually-show
{
	clip 					:auto;
	width 					:auto;
	height 					:auto;
	position 				:static;
}

%empty-generated-content
{
	content 				:"";
	display 				:block;
}

%empty-abs-generated-content
{
	@extend %empty-generated-content;

	position 				:absolute;
}

%invisible
{
	opacity 				:0;
	visibility 				:hidden;
}

%opaque
{
	opacity 				:1;
	visibility 				:visible;
}

%prevent-text-breakouts
{
	word-wrap 				:break-word;
	hyphens 				:auto;
}

%truncate-text
{
	text-overflow 			:ellipsis;
	overflow 				:hidden;
	white-space 			:nowrap;
}

%subpixel-text
{
	-webkit-font-smoothing 	:subpixel-antialiased;
	-moz-osx-font-smoothing :auto;
	font-smoothing 			:subpixel-antialiased;
